mapper.select:
    select * from(
    select customer,
   if(sum(t1 + t2 + t3) = 0, "睡眠客",
      if(sum(t1 + t2 + t3) = 2, "活客", if(sum(t1 + t2 + t3) = 3, "有效客", "其它"))) as status
from (
   select customer,
                   if(createtime between date_sub(now(), INTERVAL 30 DAY) and now(), 1, 0) as t1,
                   if(createtime between date_sub(now(), INTERVAL 60 DAY) and date_sub(now(), INTERVAL 30 DAY), 1,
                      0)                                                                   as t2,
                   if(createtime between date_sub(now(), INTERVAL 90 DAY) and date_sub(now(), INTERVAL 60 DAY), 1,
                      0)                                                                   as t3
   from settlementnew
  where customer=#{customer}
 ) t)aa
 inner join (
    select customer,createtime,projectname,money,round(times,2) times,round(hand,2) hand
    from settlementnew
    where customer=#{customer} and shopid=#{shopid} and category in ('美容','美体','仪器') and createtime between #{starttime} and #{endtime}

        and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}

    union all
    select '总计' as customer,''as createtime,''as projectname,sum(money) as money,round(sum(times),2) as times,round(sum(hand),2) as hand
    from settlementnew
    where customer=#{customer} and shopid=#{shopid} and category in ('美容','美体','仪器') and createtime between #{starttime} and #{endtime}

        and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}

    ) bb where aa.customer=bb.customer

    select * from(
    select customer,
   if(sum(t1 + t2 + t3) = 0, "睡眠客",
      if(sum(t1 + t2 + t3) = 2, "活客", if(sum(t1 + t2 + t3) = 3, "有效客", "其它"))) as status
from (
   select customer,
                   if(createtime between date_sub(now(), INTERVAL 30 DAY) and now(), 1, 0) as t1,
                   if(createtime between date_sub(now(), INTERVAL 60 DAY) and date_sub(now(), INTERVAL 30 DAY), 1,
                      0)                                                                   as t2,
                   if(createtime between date_sub(now(), INTERVAL 90 DAY) and date_sub(now(), INTERVAL 60 DAY), 1,
                      0)                                                                   as t3
   from settlementnew
  where customer=#{customer}
 ) t)aa
 inner join (
    select customer,createtime,projectname,money,round(times,2) times,round(hand,2) hand
    from settlementnew
    where customer=#{customer} and shopid=#{shopid} and consumptioncategory in ('现金卡','现金产品','现金实操') and createtime between #{starttime} and #{endtime}

        and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}

    union all
    select '总计' as customer,''as createtime,''as projectname,sum(money) as money,round(sum(times),2) as times,round(sum(hand),2) as hand
    from settlementnew
    where customer=#{customer} and shopid=#{shopid} and consumptioncategory in ('现金卡','现金产品','现金实操') and createtime between #{starttime} and #{endtime}

        and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}

    ) bb where aa.customer=bb.customer

select * from(
select customer,
       if(sum(t1 + t2 + t3) = 0, "睡眠客",
          if(sum(t1 + t2 + t3) = 2, "活客", if(sum(t1 + t2 + t3) = 3, "有效客", "其他"))) as status
from (
       select distinct customer,
                       if(createtime between date_sub(now(), INTERVAL 30 DAY) and now(), 1, 0) as t1,
                       if(createtime between date_sub(now(), INTERVAL 60 DAY) and date_sub(now(), INTERVAL 30 DAY), 1,
                          0)                                                                   as t2,
                       if(createtime between date_sub(now(), INTERVAL 90 DAY) and date_sub(now(), INTERVAL 60 DAY), 1,
                          0)                                                                   as t3
       from settlementnew
     ) t
group by customer) aa
inner join(
select customer,createtime,projectname,money,times,hand
from settlementnew
where customer=#{customer} and shopid=#{shopid} and createtime between #{start} and #{end}

    and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}

order by createtime desc) bb on aa.customer=bb.customer

select customer, status, createtime, projectname, money, times, hand from(
select customer,
       if(sum(t1 + t2 + t3) = 0, "睡眠客",
          if(sum(t1 + t2 + t3) = 2, "活客", if(sum(t1 + t2 + t3) = 3, "有效客", "其他"))) as status
from (
       select distinct customer,
                       if(createtime between date_sub(now(), INTERVAL 30 DAY) and now(), 1, 0) as t1,
                       if(createtime between date_sub(now(), INTERVAL 60 DAY) and date_sub(now(), INTERVAL 30 DAY), 1,
                          0)                                                                   as t2,
                       if(createtime between date_sub(now(), INTERVAL 90 DAY) and date_sub(now(), INTERVAL 60 DAY), 1,
                          0)                                                                   as t3
       from settlementnew
     ) t
group by customer) aa
inner join(
select customer,createtime,projectname,money,times,hand
from settlementnew
where shopid=#{shopid} and createtime between #{start} and #{end}

    and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}

order by createtime desc) bb on aa.customer=bb.customer

select customer,count(customer) as times
from settlementnew
where shopid=#{shopid} and createtime between #{start} and  #{end}

    and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}

group by customer
order by times desc;

select * from( select customer,sum(money) as smoney from settlementnew where shopid=#{shopid} and consumptioncategory in ('卡扣产品','卡扣实操','现金产品','现金实操','赠送实操') and createtime between #{start} and #{end}
and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}
group by customer) t order by smoney desc

select * from( select customer,sum(money) as smoney from settlementnew where shopid=#{shopid} and consumptioncategory in ('现金产品','现金卡','现金实操') and createtime between #{start} and #{end}
and beautician1=#{staffid} or beautician2=#{staffid} or beautician3=#{staffid} or beautician4=#{staffid} or consultant=#{consultant}
group by customer) t order by smoney desc
