mapper.select:
select if(count(t.createtime) = 0, -1, datediff(now(), createtime)) lastsdays
from (
         select createtime
         from settlementnew
         where shopid = #{shopid}
           and customer = #{customer}
         order by createtime desc
         limit 1
     ) t
group by t.createtime;



select if(sumcourses=0,-2,sumcourses-count) lasttimes
from (
select count(*) count
from settlementnew
where customer =    #{customer}
and shopid =      #{shopid}
and projectname = #{projectname}
and consumptioncategory = "卡扣实操"
limit 1
) t1,

(
select sum(courses) as sumcourses
from settlementnew
where customer =    #{customer}
and shopid =      #{shopid}
and projectname = #{projectname}
and category = "卡"
and consumptioncategory = "卡扣卡") t2;
