mapper.select:
select if(count(t.createtime) = 0, -1, datediff(now(), createtime)) lastsdays
from (
         select createtime
         from settlementnew
         where shopid = #{shopid}
           and customer = #{customer}
         order by createtime desc
         limit 1
     ) t
group by t.createtime;



select if(count(t1.onetimemoney) != 0 and count(t2.summoney) != 0, (summoney - alreadyconsumed) / onetimemoney,
          if(count(t2.summoney) = 0, -1, -2)) lasttimes
from (
         select money as onetimemoney, sum(money) as alreadyconsumed
         from settlementnew
         where customer = #{customer}
           and shopid = #{shopid}
           and projectname = #{projectname}
           and consumptioncategory = "卡扣实操"
         limit 1
     ) t1,

     (
         select sum(money) as summoney
         from settlementnew
         where customer = #{customer}
           and shopid = #{shopid}
           and projectname = #{projectname}
           and category = "卡"
           and consumptioncategory = "卡扣卡") t2;
